import { useEffect, useState } from 'react';
// import '../node_modules/bootstrap/dist/css/bootstrap.css';
// import './common.css';
import { useHistory } from 'react-router-dom';

function Login(){

  const [credentials, setcredentials] = 
  useState({username: "", password:""});

const [message, setmessage] = useState("");

const history = useHistory(); 

useEffect(()=>{
if(message!="")
{
setTimeout(() => {
  setmessage("");
}, 3000);
}
}, [message])

const OnTextChange =(args)=>{
var copyOfcredentials = {...credentials};
copyOfcredentials[args.target.name] = 
                  args.target.value;
setcredentials(copyOfcredentials);
}
const SignIn = ()=>{
debugger;
var helper = new XMLHttpRequest();

helper.onreadystatechange=()=>{
if(helper.readyState == 4 &&
  helper.status == 200)
  {
      debugger;
     var responseReceived = 
      JSON.parse( helper.responseText);
      if(responseReceived.isValid 
          == 'true')
      {
sessionStorage.setItem("isloggedin", true);
sessionStorage.setItem("username",credentials.username);
      props.setUserName(credentials.username)

      history.push("/");

      }
      else
      {
          setmessage("Credentials are invalid!");
          setcredentials({username: "", password:""});
      }
}
}
helper.open("POST", 
"http://127.0.0.1:9999/login");

helper.setRequestHeader("Content-Type", "application/json")

var credentialsInString = JSON.stringify(credentials);

var credentialsInEncoded = window.btoa(credentialsInString)

var details ={"credentials":
credentialsInEncoded }
helper.send(JSON.stringify(details));
}

        return(
            <div className="container">
            <div className="row justify-content-center">
              <div className="col-md-6" Style="margin-top: 100px; max-width: 400px;">
                    <center>
                <h3 className="mb-3">Login</h3>
                    </center>
                <form>
                  <div className="form-group">
                    <label for="username">Username:</label>
                    <input type="text" className="form-control" id="username" placeholder="Enter username"value={credentials.username} 
                        name='username'
                        onChange={OnTextChange}/>
                  </div>
                  <div className="form-group">
                    <label for="password">Password:</label>
                    <input type="password" className="form-control" id="password" placeholder="Enter password"value={credentials.password}
                            name='password'
                            onChange={OnTextChange}/>
                  </div>
                  <center>
                  <button type="submit" className="btn btn-primary"onClick={SignIn}>Login</button>&nbsp;&nbsp;
                  <a type="submit" className="btn btn-danger" href="./Auth/otp.js">Forgot Password</a>
                  </center>
                </form>
              </div>
            </div>
          </div>
        )
}
export default Login;
